        if (!status) {
            logStatus("End TestCase", "Test Case 12: Verify user Registration : ",
                    status ? "PASS" : "FAIL");
            takeScreenshot(driver, "Testcasefailure", "Unable_to_perform_login");
            return false;
        }

        // Go to the home page
        Home homePage = new Home(driver);
